svg.script:
function showTooltip(evt, text) {
  var svg = evt.target.ownerSVGElement;
  var tooltip = svg.getElementById('tooltip');
  var tooltipText = svg.getElementById('tooltip-text');
  var tooltipBg = svg.getElementById('tooltip-bg');

  while (tooltipText.firstChild) {
    tooltipText.removeChild(tooltipText.firstChild);
  }

  var lines = text.split('\n');
  for (var i = 0; i < lines.length; i++) {
    var tspan = document.createElementNS('http://www.w3.org/2000/svg', 'tspan');
    tspan.setAttribute('x', '0');
    tspan.setAttribute('dy', i === 0 ? '0' : '1.2em');
    tspan.textContent = lines[i];
    tooltipText.appendChild(tspan);
  }

  var pt = svg.createSVGPoint();
  pt.x = evt.clientX;
  pt.y = evt.clientY;
  var ctm = svg.getScreenCTM().inverse();
  var loc = pt.matrixTransform(ctm);

  var padding = 6;
  var bbox = tooltipText.getBBox();
  tooltipBg.setAttribute('x', bbox.x - padding);
  tooltipBg.setAttribute('y', bbox.y - padding);
  tooltipBg.setAttribute('width', bbox.width + 2 * padding);
  tooltipBg.setAttribute('height', bbox.height + 2 * padding);

  tooltip.setAttribute('transform', 'translate(' + (loc.x + 15) + ',' + (loc.y - 10) + ')');
  tooltip.setAttribute('visibility', 'visible');

  var g = evt.currentTarget.closest('.event-group');
  if (g) {
    g.setAttribute('data-original-transform', g.getAttribute('transform') || '');
    g.setAttribute('transform', (g.getAttribute('transform') || '') + ' scale(1.03)');
  }
}

function hideTooltip(evt) {
  var svg = evt.target.ownerSVGElement;
  var tooltip = svg.getElementById('tooltip');
  tooltip.setAttribute('visibility', 'hidden');

  var g = evt.currentTarget.closest('.event-group');
  if (g) {
    var orig = g.getAttribute('data-original-transform') || '';
    g.setAttribute('transform', orig);
  }
}
